pwd=`pwd`;echo python/Dockerfile-2.7>>$pwd/folder; cat $pwd/folder | cut -d '/' -f1>>$pwd/path ;cat $pwd/folder | cut -d '-' -f2>>$pwd/ver ; cat $pwd/folder | cut -d '/' -f2>>$pwd/dockerfile; echo "ver="; cat $pwd/ver;  echo "dockerfile="; cat $pwd/dockerfile ; echo "path=";cat $pwd/path; path=$(cat $pwd/path);  if [[ $(cat $pwd/dockerfile) == 'Dockerfile' ]]; then version=$(cat $path/version);  else ver=-$(cat $pwd/ver) && cd $path && version=$(cat version$ver); fi;  echo "version=$version" >> $GITHUB_ENV ;rm $pwd/ver; rm $pwd/folder; rm $pwd/path; rm $pwd/dockerfile;
